import {Button, Nav, Navbar, NavDropdown, NavItem, Tab, Tabs} from "react-bootstrap";
import logo from "../project-images/nwmsu-logo2.png";
import React, {useEffect, useState} from "react";
import Grid from "../components/Grid";
import {useNavigate} from "react-router-dom";
import GenericModal from "./PostProjectModal";

const Dashboard = () => {

    const [showProjectModal, setShowProjectModal] = useState(false);

    const [showProfile, setShowProfile] = useState(false);

    const [projects, setProjects] = useState([]);

    useEffect(() => {
        fetchProjects();
    }, []);

    const navigate = useNavigate();

    const fetchProjects = async () => {
        try {
            const response = await fetch(`/project/getProjectsByName`, {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem('authToken')}`
                },
            });

            if (response.ok) {
                const data = await response.json();
                console.log(data)
                setProjects(data);
            } else {
                console.error('Error fetching data:', response.statusText);
                navigate('/')
            }
        } catch (error) {
            console.error('An error occurred:', error);
        }
    };

    const onLogout = function () {

        navigate('/');
        localStorage.clear();
    }


    return (
        <div className="container col-md-12">

            <Navbar bg="light" style={{height: 100}}>
                <Navbar.Brand href="#home">
                    <img
                        alt=""
                        src={logo}
                        width="160"
                        height="50"
                        className="d-inline-block align-content-between"
                    />{' '}
                    <span className="font-weight-bold">Project Explorer</span>
                </Navbar.Brand>
                <Navbar.Toggle/>
                <Navbar.Collapse className="justify-content-end">
                    <Navbar.Text>
                        Signed in as: <a href="#login">{localStorage.getItem('username')}</a>
                    </Navbar.Text>
                    <NavDropdown title='' id="collasible-nav-dropdown">
                        <NavDropdown.Item onClick={() => setShowProfile(true)}>Profile</NavDropdown.Item>
                        <NavDropdown.Item onClick={onLogout}>Logout</NavDropdown.Item>
                    </NavDropdown>

                </Navbar.Collapse>
            </Navbar>
            <div className="row">
                <div className=" bg-dark min-vh-100 min-vw-1 align-items-stretch" style={{paddingTop: '30px'}}>

                    <Nav className="flex-column">
                        <Nav.Link onClick={() => setShowProfile(true)}>
                            <div className="d-flex flex-column align-items-center justify-content-between">
                                <i className="fa fa-user-circle fa-2x" aria-hidden="true"></i><p>Profile</p>
                            </div>
                        </Nav.Link>
                        <Nav.Link>
                            <div className="d-flex flex-column align-items-center">
                                <i className="fa fa-calendar fa-2x" aria-hidden="true"></i><p>Calendar</p>
                            </div>
                        </Nav.Link>
                        <Nav.Link>
                            <div className="d-flex flex-column align-items-center">
                                <i className="fa fa-inbox fa-2x" aria-hidden="true"></i><p>Inbox</p>
                            </div>
                        </Nav.Link>
                        <Nav.Link onClick={() => setShowProjectModal(true)}>
                            <div className="d-flex flex-column align-items-center">
                                <i className="fa fa-plus-circle fa-2x" aria-hidden="true"></i><p>Post</p>
                            </div>
                        </Nav.Link>
                        <Nav.Link>
                            <div className="d-flex flex-column align-items-center">
                                <i className="fa fa-dashboard fa-2x" aria-hidden="true"></i><p>History</p>
                            </div>
                        </Nav.Link>
                        <Nav.Link>
                            <div className="d-flex flex-column align-items-center">
                                <i className="fa fa-question-circle fa-2x" aria-hidden="true"></i><p>Help</p>
                            </div>
                        </Nav.Link>
                    </Nav>
                </div>
                <div className="col-md-11">
                    <div className="container col-md-12 d-flex justify-content-end p-3">
                        <Button variant="primary" onClick={() => setShowProjectModal(true)}>
                            <i className="fa fa-plus" aria-hidden="true"></i> Post Project</Button>
                    </div>
                    <div className='container col-md-12'>
                        <Tabs defaultActiveKey="myProjects" id="uncontrolled-tab-example">
                            <Tab eventKey="myProjects" title="My Projects">
                                <div className="col-md-12 p-5">
                                    <Grid data={projects} title="My Projects"></Grid>
                                </div>
                            </Tab>
                            <Tab eventKey="approved" title="Approved">
                                <div className="col-md-12 p-5">
                                    <Grid data={projects.filter(project => project.status !== 'Pending')}></Grid>
                                </div>
                            </Tab>
                        </Tabs>
                    </div>
                </div>
            </div>
            <GenericModal show={showProjectModal} type='postProject' title="Post Project"
                          onHide={() => setShowProjectModal(false)}></GenericModal>
            <GenericModal show={showProfile} type='profile' title="Profile"
                          onHide={() => setShowProfile(false)}></GenericModal>
        </div>
    )
}

export default Dashboard;
